port java.io.BufferedReader;\nimport java.io.IOException;\nimport java.io.InputStreamReader;\nimport java.util.ArrayList;\nimport java.util.Collections;\nimport java.util.HashSet;\nimport java.util.List;\n\n/**\n * @description: ç¦»æ•£åŒ–\n * https://www.acwing.com/activity/content/problem/content/836/\n * ç¬¬ä¸€æ­¥å…ˆå°†æ‰€æœ‰ç”¨åˆ°çš„ä¸‹æ ‡ä»¥åŠvalueå­˜å‚¨è¿›ä¸€ä¸ªæ•°ç»„ä¸­\n * å¯¹æ•°ç»„è¿›è¡Œæ’åºå»é‡\n * åœ¨é€šè¿‡äºŒåˆ†æ¥æŸ¥æ‰¾éœ€è¦æ•°å­—åœ¨æ•°ç»„ä¸­çš„ä¸‹æ ‡ â€æ˜ å°„â€œ\n * å†è¿›è¡Œå¤„ç†å³å¯\n * @author: kudaompq\n * @date: 2022/1/3 11:32\n * @version: v1.0\n */\n\npublic class Discretization {\n\n    private static List<Integer> alls = new ArrayList<>();\n\n    public static void main(String[] args) throws IOException {\n        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));\n        String[] temp = reader.readLine().split(\" \");\n        int n = Integer.parseInt(temp[0]);\n        int m = Integer.parseInt(temp[1]);\n        int[] a = new int[400010];\n        int[] s = new int[400010];\n        ArrayList<Pair> adds = new ArrayList<>();\n        ArrayList<Pair> querys = new ArrayList<>();\n        for (int i = 0; i < n; i++) {\n            temp = reader.readLine().split(\" \");\n            int x = Integer.parseInt(temp[0]);\n            int y = Integer.parseInt(temp[1]);\n            adds.add(new Pair(x,y));\n            alls.add(x);\n            alls.add(y);\n        }\n        for (int i = 0; i < m; i++) {\n            temp = reader.readLine().split(\" \");\n            int x = Integer.parseInt(temp[0]);\n            int y = Integer.parseInt(temp[1]);\n            querys.add(new Pair(x,y));\n            alls.add(x);\n            alls.add(y);\n        }\n        HashSet<Integer> set = new HashSet<>(alls);\n        alls = new ArrayList<>(set);\n        Collections.sort(alls);\n\n        for (Pair add : adds) {\n            int x = find(add.first);\n            a[x] += add.second;\n        }\n        for (int i = 1; i <= alls.siz