 res = new ArrayList<>();\n        // å…ˆå¯¹åˆ—è¡¨è¿›è¡ŒæŒ‰ç…§å·¦ç«¯ç‚¹è¿›è¡Œæ’åº\n        Collections.sort(segs, new Comparator<Pair>() {\n            @Override\n            public int compare(Pair o1, Pair o2) {\n                if (o1.first == o2.first) return  o1.second - o2.second;\n                return o1.first - o2.first;\n            }\n        });\n\n        int start = (int)-2e9 , end = (int)-2e9;\n        for (Pair seg : segs) {\n            if (end < seg.first){\n                if (start != (int)-2e9) res.add(new Pair(start,end)); // è®°å½•åŒºé—´\n                start = seg.first;\n                end = seg.second;\n            }\n            else end = Math.max(end, seg.second);\n        }\n        if (start != (int)-2e9) res.add(new Pair(start,end));\n        System.out.println(res.size());\n    }\n\n    static class Pair{\n        int first;\n        int second;\n        public Pair(int first,int second){\n            this.first = first;\n            this.second = second;\n        }\n\n        @Override\n        public String toString() {\n            return \"Pair{\" +\n                    \"first=\" + first +\n                    \", second=\" + second +\n                    \'}\';\n        }\n    }\n}\n```\n\n## æ•°æ®ç»“æ„\n\n### é“¾è¡¨\n\nåœ¨ç®—æ³•ä¸­é€šå¸¸ç”¨æ•°ç»„æ¥æé«˜é€Ÿåº¦\n\n> å•é“¾è¡¨\n\n```java\nimport java.io.BufferedReader;\nimport java.io.IOException;\nimport java.io.InputStreamReader;\n\n/**\n * @description: å•é“¾è¡¨\n * https://www.acwing.com/problem/content/828/\n * @author: kudaompq\n * @date: 2022/1/4 10:51\n * @version: v1.0\n */\n\npublic class SingleLinkedList {\n\n     static final int N = (int)1e6 + 10;\n     static int head; // è¡¨ç¤ºå¤´ç»“ç‚¹çš„ä¸‹æ ‡\n     static int[] e = new int[N]; // è¡¨ç¤ºèŠ‚ç‚¹içš„å€¼\n     static int[] ne = new int[N]; // è¡¨ç¤ºèŠ‚ç‚¹içš„nextæŒ‡é’ˆæ˜¯å¤šå°‘\n     static int idx; // å­˜å‚¨å½“å‰å·²ç»ç”¨åˆ°äº†å“ªä¸ªç‚¹\n\n    public static void main(String[] args) throws IOException {\n        BufferedReader reader = new BufferedReader(new InputStreamReader(System.in));\n        init();\n        int n = Integer.parseInt(reader.readLine());\n        while (n -- > 0){\n            String[] temp = reader.readLine().split(\" \");\n            if (temp[0].equals(\"H\")){\n                add_to_head(Integer.parseInt(temp[1]));\n            }else if (temp[0].equals(\"D\")){\n                int k = Integer.parseInt(temp[1]);\n                if (k == 0) {head = ne[head]; continue;}  // å½“k=0çš„æ—¶å€™å°±è¦å»é™¤å¤´èŠ‚ç‚¹\n                remove(k - 1);\n            }else{\n                add(Integer.parseInt(temp[1]) - 1,Integer.parseInt(temp[2]));\n            }\n        }\n        for (int i = head; i != -1 ; i = ne[i]) {\n            System.out.print(e[i] + \" \");\n        }\n    }\n\n    // åˆå§‹åŒ–\n    private static void init(){\n        head = -1;\n        idx = 0;\n    }\n\n    // å°†xæ’å…¥åˆ°å¤´ç»“ç‚¹\n    private static void add_to_head(int x){\n        e[idx] = x;\n        ne[idx] = head;\n        head = idx;\n        idx++;\n    }\n\n    // è¡¨ç¤ºåœ¨ç¬¬ k ä¸ªæ’å…¥çš„æ•°åé¢æ’å…¥ä¸€ä¸ªæ•° x\n    private static void add(int k,int x){\n        e[idx] = x;\n        ne[idx] = ne[k];\n        ne[k] = idx;\n        idx++;\n    }\n\n    // åˆ é™¤ç¬¬ k ä¸ªæ’å…¥çš„æ•°åé¢çš„æ•°\n    private static void remove(int k){\n        ne[k] = ne[ne[k]];\n    }\n}\n```\n\n> åŒå‘é“¾è¡¨\n\n```java\nimport java.io.BufferedReader;\nimport java.io.IOException;\nimport java.io.InputStreamReader;\n\n/**\n * @description: åŒå‘é“¾è¡¨\n * https://www.acwing.com/problem/content/829/\n * @author: kudaompq\n * @date: 2022/1/4 11:30\n * @version: v1.0\n */\n\npublic class DoubleLinkedList {\n    static final int N = (int) 1e5 + 10;\n    static int[] e = new int[N];\n    static int[] l = new int[N];\n    static int[] r = new int[N];\n    static int idx;\n    public static void main(String[] args) throws IOException {\n        init();\n        BufferedReader reader = new BufferedReader(ne